mapper.select:
SELECT er.role_id
FROM t_role_user er
WHERE er.user_id = #{userId}

SELECT t_user.user_id, t_user.login_name, t_user.login_pwd, t_user.actual_name, t_user.phone, t_user.disabled_flag, t_user.remark, t_user.update_time, t_user.create_time FROM t_role_user left join t_user on t_role_user.user_id = t_user.user_id left join t_role on t_role_user.role_id = t_role.role_id
AND ( INSTR(t_useractual_name,#{queryForm.keywords}) OR INSTR(t_userphone,#{queryForm.keywords}) OR INSTR(t_userlogin_name,#{queryForm.keywords}) )
AND t_role_user.role_id = #{queryForm.roleId}

mapper.delete:
DELETE
FROM t_role_user
WHERE role_id = #{roleId}
  and user_id = #{userId}

DELETE FROM t_role_user
WHERE role_id = #{roleId} and user_id in

    #{item}
