mapper.select:
SELECT
t_user.*
FROM t_user


        AND (
        INSTR(t_user.actual_name,#{queryForm.keyword})
        OR INSTR(t_user.phone,#{queryForm.keyword})
        OR INSTR(t_user.login_name,#{queryForm.keyword})
        )


        AND t_user.disabled_flag = #{queryForm.disabledFlag}


        AND t_user.deleted_flag = #{queryForm.deletedFlag}

mapper.update:
UPDATE t_user
SET disabled_flag = #{disabledFlag}
WHERE employee_id = #{employeeId}

mapper.select:
SELECT *
FROM t_user

    login_name = #{loginName}

        AND disabled_flag = #{disabledFlag}

SELECT *
FROM t_user

    actual_name = #{actualName}

        AND disabled_flag = #{disabledFlag}

SELECT *
FROM t_user

    phone = #{phone}

        AND disabled_flag = #{disabledFlag}

SELECT count(1) FROM t_user
WHERE
department_id = #{departmentId}

SELECT *
FROM t_user

    department_id = #{departmentId}

        AND disabled_flag = #{disabledFlag}


ORDER BY create_time DESC

SELECT * FROM t_user

    actual_name = #{actualName}
    AND department_id in

        #{item}


        AND disabled_flag = #{disabledFlag}

SELECT employee_id
FROM t_user

    department_id IN

        #{item}


        AND disabled_flag = #{disabledFlag}


ORDER BY create_time DESC

SELECT employee_id
FROM t_user


        AND disabled_flag = #{disabledFlag}

SELECT employee_id
FROM t_user

    department_id = #{departmentId}

        AND disabled_flag = #{disabledFlag}


ORDER BY create_time DESC

SELECT * FROM t_user where employee_id IN
#{item}
ORDER BY create_time DESC

SELECT t_user.*
FROM t_user
where t_user.employee_id = #{employeeId}

SELECT
t_user.*
FROM t_user


        AND t_user.disabled_flag = #{disabledFlag}


        AND t_user.deleted_flag = #{deletedFlag}

mapper.update:
UPDATE t_user
SET login_pwd = #{password}
WHERE employee_id = #{employeeId}
